#pragma config(Hubs,  S1, HTMotor,  HTMotor,  none,     none)
#pragma config(Sensor, S1,     ,               sensorI2CMuxController)
#pragma config(Motor,  mtr_S1_C1_1,     FrontLeft,     tmotorTetrix, PIDControl, encoder)
#pragma config(Motor,  mtr_S1_C1_2,     FrontRight,    tmotorTetrix, PIDControl, encoder)
#pragma config(Motor,  mtr_S1_C2_1,     BackLeft,      tmotorTetrix, PIDControl, reversed, encoder)
#pragma config(Motor,  mtr_S1_C2_2,     BackRight,     tmotorTetrix, PIDControl, reversed, encoder)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "JoystickDriver.c"
#include "common.c"

//Constants
#include "constants.h"

//Initialize our globals
byte joystickScale = JOYSTICKHIGH; //Used to scale down robot movements

bool forwardlock = true; //Used for direction locking
bool sidelock = true;

task joystickControl(){ //Asynchronous task for joystick control
	while(true){
		//Joystick 1 - Driver

		//Deadband between -15 to 15
		//Scale linearly for all other values
	 	float x1, y1, x2, y2; // The x and y values for the two joysticks.

		/* The following two if statements check to make sure the joystick is above the deadband, and then converts the joystick value to a 100 scale.
	 	 * After converting it stores the value into the correct x or y value. */
		if(((joystick.joy1_x1 > 15 || joystick.joy1_x1 < -15) || (joystick.joy1_y1 > 15 || joystick.joy1_y1 < -15)) || ((joystick.joy1_x2 > 15 || joystick.joy1_x2 < -15) || (joystick.joy1_y2 > 15 || joystick.joy1_y2 < -15)))
		{
			x1 = joystick.joy1_x1 / 128.0 * 100;
			y1 = joystick.joy1_y1 / 128.0 * 100;
			x2 = joystick.joy1_x2 / 128.0 * 100;
			y2 = joystick.joy1_y2 / 128.0 * 100;
		}
		else
		{
			x1 = 0;
			y1 = 0;
			x2 = 0;
			y2 = 0;
		}

		/* The following if statements check to see if the first controller's joysticks are being pressed, and if they are will assign the proper motor movement.
		 * If both the left joystick and right joystick are activated, it will combine them to allow forward movement and turning.*/
		if((x1 < -15 || x1 > 15 || y1 < -15 || y1 > 15) || (x2 < -15 || x2 > 15))
		{
			motor[FrontLeft] = -x2 + (-y1 + -x1);
			motor[FrontRight] = -x2 + (y1 + -x1);
			motor[BackLeft] = -x2 + (-y1 + x1);
			motor[BackRight]= -x2 + (y1 + x1);
		}
		else  // Resets the motor values when the user finishes moving the joysticks.
		{
			motor[FrontLeft] = 0;
			motor[FrontRight] = 0;
			motor[BackLeft] = 0;
			motor[BackRight]= 0;
		}
	}
}

/*task armControl() { //Another asynchronous task to move the arm
	while(true) {
		//Joystick 2 - Operator
		break;
	}
} */

task main() {
	//Initialize
	motor[FrontLeft] = motor[FrontRight] = motor[BackLeft] = motor[BackRight] = 0; //Turn off the motors
	nMotorEncoder[FrontLeft] = nMotorEncoder[FrontRight] = nMotorEncoder[BackLeft] = nMotorEncoder[BackRight] = 0; // Might as well reset the encoders too

	waitForStart();
	StartTask(joystickControl); //Go ahead and start critical joystick functions in their own task
	// StartTask(armControl); //Start arm functions in their own task too
	while(true) {
		//Joystick 1 - Driver

		if(joy1Btn(6)) //If the driver is pressing button 6, scale down robot movements
			joystickScale = JOYSTICKLOW;
		else //Else leave at full speed
			joystickScale = JOYSTICKHIGH;

		if(joy1Btn(5)) //If the driver is pressing button 5, lock to y-axis movement
			sidelock = false;
		else
			sidelock = true;

		if(joy1Btn(7)) //If the driver is pressing button 7, lock to x-axis movement
			forwardlock = false;
		else
			forwardlock = true;
	}
}
